mapper.insert:
INSERT INTO file (
	SEQ_FILE_MST
	, NAME
	, NAME_ORIGNAL
	, DIR_PATH
	, URL_PATH
	, SIZE
	, EXTENSION
	, CONTENT_TYPE
	, USER_ORDER
	, REG_ID
	, REG_TIME
	, MOD_ID
	, MOD_TIME
	, DEL_ID
	, DEL_TIME
) VALUES (
	#{seq_file_mst}
	, #{name}
	, #{name_original}
	, #{dir_path}
	, #{url_path}
	, #{size}
	, #{extension}
	, #{content_type}
	, #{user_order}
	, #{uid}
	, SYSDATE()
	, NULL
	, NULL
	, NULL
	, NULL
)

	SELECT LAST_INSERT_ID()

mapper.update:
UPDATE file SET
	USER_ORDER = #{user_order}
	, MOD_ID = #{mod_id}
	, MOD_TIME = SYSDATE()
WHERE URL_PATH = #{url_path}

UPDATE file SET
	DEL_ID = #{del_id}
	, DEL_TIME = SYSDATE()
WHERE SEQ = #{seq}
